import mongoose from 'mongoose';
import { filterByDate } from '../helper';

/**
 *  @brief API to filter the turbines for the map view
 *  @returns void
 *  @param request  request from frontend+
 *  @param response to frontend a mongoose.Document[]
 */
export async function getTurbinesByDate(request: any, response: any): Promise<void> {
    const from: Date = new Date(request.query.from);
    const until: Date = new Date(request.query.until);
    const filterProperty: string = request.query.filterProperty;
    const filteredByDate: mongoose.Document[] = await filterByDate(until, from, filterProperty);
    response.send(filteredByDate);
}

import { Document } from 'mongoose';
import { WKA } from "./db-models/wka-model";

/**
 *  @brief helper function to convert the date string to a Date Object
 *  @returns Date
 *  @param dateString from type string
 */
export function convertToDate(dateString: string): Date {
    const parts: string[] = dateString.split('.');
    const day: string = parts[0];
    const month: string = parts[1];
    const year: string = parts[2];
    return new Date(year + "-" + month + "-" + day);
}

/**
 *  @brief function to filter for turbines by date 
 *  @returns void
 *  @param until the date request from frontend
 *  @param from the date request from frontend
 *  @param filterProperty the filter property request from frontend
 */
export async function filterByDate(until: Date, from: Date, filterProperty: string): Promise<Document[]> {

    const allTurbines = await WKA.find({}).exec();
    const turbinesInInterval: Document[] = allTurbines.filter(turbine => {
        const dateString: string = turbine.get(filterProperty);
        const date: Date = convertToDate(dateString);
        return date >= from && date <= until;
    });
    //console.log('Turbines matched date: ', turbinesInInterval.length);
    return turbinesInInterval;
}

/**
 *  @brief helper function to get all Area codes from database
 *  @returns getAllAreaCodes array of strings
 */
export async function getAllAreaCodes(): Promise<string[]> {
    const getAllAreaCodes: string[] = await WKA.distinct('PLZ,C,5').exec();

    return getAllAreaCodes.map(entry => String(entry));
}

/**
 *  @brief helper function which gets all documents from datebase
 *  @returns Document
 */
export async function findAllDocuments(): Promise<Document[]> {
    return await WKA.find({}).exec();
}

import mongoose from 'mongoose';
import { filterByDate } from '../helper';

/**
 *  @brief API to return all power values filtered by date
 *  @returns void
 *  @param request  request from frontend
 *  @param response to frontend the sum as type number
 */
export async function getFullPowerByDate(request: any, response: any): Promise<void> {
    const from: Date = new Date(request.query.from);
    const until: Date = new Date(request.query.until);
    const filterProperty: string = request.query.filterProperty;
    const filteredByDate: mongoose.Document[] = await filterByDate(until, from, filterProperty);
    
    const sum: number = filteredByDate
        .map(turbine => {
            return Number(turbine.get("Leistung,N,13,3").replace(',', '.'))
        })
        .reduce((sum, current) => sum + current, 0);

    response.send({ sum });
}

import mongoose from 'mongoose';
import { filterByDate, convertToDate } from '../helper';

/**
 *  @brief API to return power and date of years
 *  @returns void
 *  @param request  request from frontend
 *  @param response returns an array 
*/
export async function getPowerPerYears(request: any, response: any): Promise<void> {
    const from: Date = new Date(request.query.from);
    const until: Date = new Date(request.query.until);
    const filterProperty: string = request.query.filterProperty;

    const years: any[] = [];
    for (let index: number = from.getFullYear(); index < until.getFullYear() + 1; index++) {
        years.push({ 'year': index, 'power': 0 });
    }

    const filteredByDate: mongoose.Document[] = await filterByDate(until, from, filterProperty);

    filteredByDate.forEach(turbine => {
        const power = Number(turbine.get("Leistung,N,13,3").replace(',', '.'));
        const dateString: string = turbine.get(filterProperty);
        const date: Date = convertToDate(dateString);
        const year: number = date.getFullYear();
        const actualYear = years.find(entry => year === entry.year);
        actualYear.power += power;
    });

    years.forEach(entry => {
        entry.change = entry.power;
        const actualYear = entry.year;
        const entryBefore = years.find(e => e.year === (actualYear - 1));
        const powerOfYearBefore = entryBefore?.power ? entryBefore.power : 0;

        return entry.power += powerOfYearBefore;
    });
    response.send(years);
}

import mongoose from 'mongoose';
import { filterByDate } from "../helper";

/**
 *  @brief API to return all hub heights and rotor diameters
 *  @returns void
 *  @param request  request from frontend
 *  @param response returns an array 
 */
export async function getHubsAndRotorsByDate(request: any, response: any): Promise<void> {
    const from: Date = new Date(request.query.from);
    const until: Date = new Date(request.query.until);
    const filterProperty: string = request.query.filterProperty;
    const filteredByDate: mongoose.Document[] = await filterByDate(until, from, filterProperty);
    const result: any[] = filteredByDate.map(turbine => {
        const hub: number = Number(turbine.get("Nabenhoehe,N,11,2").replace(',', '.'));
        const rotor: number = Number(turbine.get("Rotordurch,N,11,2").replace(',', '.'));
        return { hub, rotor };
    });
    const filteredResultAboveZero: any[] = result.filter((tupleValue) => {
        return tupleValue['hub'] && tupleValue['rotor'] > 0;
    });

    response.send(filteredResultAboveZero);
}

